import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import ErrorMessage from "../../messages/ErrorMessage";
import { Button, Table, Row, Col } from "react-bootstrap";
import Loading from "../../messages/Loading";
import { productDeleteAction } from "./../../actions/productListActions";
import {
	productListAction,
	productCreateAction,
} from "../../actions/productListActions";
import { LinkContainer } from "react-router-bootstrap";
import {
	PRODUCT_CREATE_RESET,
	PRODUCT_DELETE_RESET,
} from "./../../constants/constants";
import Paginate from "../Paginate";
const ProductListScreen = ({ history }) => {
	const dispatch = useDispatch();

	//for an individual user
	const userLogin = useSelector((state) => state.userLogin);
	const { userInfo } = userLogin;
	const productList = useSelector((state) => state.productList);
	const { products, page, pages } = productList;
	const productDelete = useSelector((state) => state.productDelete);
	const {
		success: successDelete,
		error: errorDelete,
		loading: loadingDelete,
	} = productDelete;

	const productCreate = useSelector((state) => state.productCreate);
	const {
		success: successCreate,
		error: errorCreate,
		loading: loadingCreate,
		product: createdProduct,
	} = productCreate;

	useEffect(() => {
		// if (successDelete) {
		// 	dispatch(productListAction());
		// }
		//only display or list all products if the user is an admin
		//we are doing userInfo && userInfo.isAdmin to avoid errors

		dispatch({ type: PRODUCT_CREATE_RESET }); //This would clear the form field after a product is created
		if (!userInfo.isAdmin) {
			history.push("/login");
		}

		//if product is successfully created
		if (successCreate) {
			//newly created product /edit
			history.push(`/admin/product/${createdProduct._id}/edit`);
		} else {
			dispatch(productListAction());
		}
	}, [
		dispatch,
		successDelete,
		createdProduct,
		successCreate,
		userInfo,
		history,
	]);

	const deleteHandler = (id) => {
		if (window.confirm("Are you sure")) {
			dispatch(productDeleteAction(id));
		} else {
			<ErrorMessage variant="danger"> Product Not Deleted</ErrorMessage>;
		}
	};

	const createProductHandler = () => {
		dispatch(productCreateAction());
	};

	return (
		<div>
			<Row className="align-items-center">
				<Col>
					<h1>Products</h1>
				</Col>
				<Col className="text-right">
					<Button onClick={createProductHandler}>
						{" "}
						<i className="fas-fa-plus"></i>Create Product
					</Button>
				</Col>
			</Row>
			{loadingCreate && <Loading />}
			{errorCreate && (
				<ErrorMessage variant="danger">{errorCreate}</ErrorMessage>
			)}
			{loadingDelete ? (
				<Loading />
			) : errorDelete ? (
				<ErrorMessage variant="danger">{errorDelete}</ErrorMessage>
			) : (
				<>
					<Table striped bordered hover responsive className="table-sm">
						<thead>
							<tr>
								<th>ID</th>
								<th>NAME</th>

								<th>PRICE</th>
								<th>CATEGORY</th>
								<th>BRAND</th>
								<th>DELIVERED</th>
								<th>edit/delete</th>
							</tr>
						</thead>
						<tbody>
							{products.map((product, idx) => (
								<tr key={idx}>
									<td>{product._id}</td>
									<td>{product.name}</td>

									<td> &#8358; {product.price.toLocaleString("en")}</td>
									<td> {product.category}</td>
									<td> {product.brand}</td>
									<td>
										{product.isDelivered ? (
											<i
												className="fas fa-check"
												style={{ color: "green" }}
											></i>
										) : (
											<i className="fas fa-times" style={{ color: "red" }}></i>
										)}
									</td>
									<td>
										<LinkContainer to={`/admin/product/${product._id}/edit`}>
											<Button variant="light" className="btn-sm">
												<i className="fas fa-edit"></i>
											</Button>
										</LinkContainer>

										<Button
											variant="trash"
											className="btn-sm"
											onClick={() => deleteHandler(product._id)}
										>
											<i style={{ color: "red" }} className="fas fa-trash"></i>
										</Button>
									</td>
								</tr>
							))}
						</tbody>
					</Table>
					<Paginate pages={pages} page={page} />
				</>
			)}
		</div>
	);
};

export default ProductListScreen;
