import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Row, Col, Image, ListGroup, Button, Card } from "react-bootstrap";
import Rating from "./Rating";
import { useSelector, useDispatch } from "react-redux";
import { productDetailsAction } from "./../../actions/productListActions.js";
const ProductScreen = ({ match }) => {
	const useDispatch = useDispatch();
	const productDetails = useSelector((state) => state.productDetails);
	const { product, loading, error } = productDetails;
	//Render the product based on IDs. FLUID: to keep in container
	useEffect(() => {
		dispatchEvent(productDetailsAction(match.params.id));
	}, [dispatch, match]);

	return (
		<>
			<Link
				className="btn btn-light my-3 shadow-sm p-1 mb-1 bg-white rounded"
				to="/"
			>
				{" "}
				Go Back
			</Link>

			<Row>
				<Col md={6} className="shadow-lg p-1 mb-5 bg-white rounded">
					<Image src={product.image} alt={product.name} fluid />
				</Col>
				<Col md={3} className="shadow-lg p-1 mb-1 bg-white rounded">
					<ListGroup variant="flush">
						<ListGroup.Item>
							<h3>{product.name}</h3>
							<Rating
								value={product.rating}
								text={`${product.numReviews} reviews`}
							/>
						</ListGroup.Item>
						<ListGroup.Item>Price: ${product.price}</ListGroup.Item>
						<ListGroup.Item>Description: ${product.description}</ListGroup.Item>
					</ListGroup>
				</Col>
				<Col md={3}>
					<Card>
						<ListGroup variant="flush">
							<ListGroup.Item>
								<Row>
									<Col>Price:</Col>
									<Col>
										<strong>${product.price}</strong>
									</Col>
								</Row>
							</ListGroup.Item>
							<ListGroup.Item>
								<Row>
									<Col>Status:</Col>
									<Col>
										{product.countInStock > 0 ? "In Stock" : "Out of Stock"}
									</Col>
								</Row>
							</ListGroup.Item>
							<Button
								disabled={product.countInStock === 0}
								type="button"
								className="btn-block"
							>
								Add To Cart
							</Button>
						</ListGroup>
					</Card>
				</Col>
			</Row>
		</>
	);
};

export default ProductScreen;
