import {
	USER_LOGIN_REQUEST,
	USER_LOGIN_SUCCESS,
	USER_LOGIN_FAIL,
	USER_LOGOUT,
	USER_DETAILS_FAIL,
	USER_DETAILS_SUCCESS,
	USER_DETAILS_REQUEST,
	USER_REGISTER_FAIL,
	USER_REGISTER_SUCCESS,
	USER_REGISTER_REQUEST,
	USER_UPDATE_PROFILE_REQUEST,
	USER_UPDATE_PROFILE_SUCCESS,
	USER_UPDATE_PROFILE_FAIL,
	USER_DETAILS_RESET,
	USER_LIST_REQUEST,
	USER_LIST_SUCCESS,
	USER_LIST_FAIL,
	USER_LIST_RESET,
	USER_DELETE_REQUEST,
	USER_DELETE_SUCCESS,
	USER_DELETE_FAIL,
	USER_UPDATE_REQUEST,
	USER_UPDATE_SUCCESS,
	USER_UPDATE_FAIL,
	USER_ADMIN_SUCCESS,
	USER_ADMIN_FAIL,
	USER_ADMIN_REQUEST,
	USER_ADMIN_RESET,
} from "./../constants/userConstants.js";
const initialState = {
	userInfo: {},
};
export const userLoginReducer = (state = initialState, action = {}) => {
	switch (action.type) {
		case USER_LOGIN_REQUEST:
			return {
				loading: true,
			};

		case USER_LOGIN_SUCCESS:
			return {
				loading: false,
				userInfo: action.payload,
			};

		case USER_LOGIN_FAIL:
			return {
				loading: false,
				error: action.payload,
			};
		case USER_LOGOUT:
			return {
				loading: false,
				userInfo: action.payload,
			};

		default:
			return state;
	}
};

export const userRegisterReducer = (state = {}, action = {}) => {
	switch (action.type) {
		case USER_REGISTER_REQUEST:
			return {
				loading: true,
			};

		case USER_REGISTER_SUCCESS:
			return {
				loading: false,
				userInfo: action.payload,
			};

		case USER_REGISTER_FAIL:
			return {
				loading: false,
				error: action.payload,
			};

		default:
			return state;
	}
};

export const userDetailsReducer = (state = { user: {} }, action = {}) => {
	switch (action.type) {
		case USER_DETAILS_REQUEST:
			return {
				...state,
				loading: true,
			};

		case USER_DETAILS_SUCCESS:
			return {
				loading: false,
				user: action.payload,
			};

		case USER_DETAILS_FAIL:
			return {
				loading: false,
				error: action.payload,
			};

		case USER_DETAILS_RESET:
			return {
				user: {},
			};

		default:
			return state;
	}
};

export const userUpdateProfileReducer = (state = {}, action = {}) => {
	switch (action.type) {
		case USER_UPDATE_PROFILE_REQUEST:
			return {
				loading: true,
			};

		case USER_UPDATE_PROFILE_SUCCESS:
			return {
				loading: false,
				userInfo: action.payload,
				success: true,
			};

		case USER_UPDATE_PROFILE_FAIL:
			return {
				loading: false,
				error: action.payload,
			};

		default:
			return state;
	}
};

const initialStatee = {
	users: [],
};
//Getting all user information for admin user
export const userListReducer = (state = initialStatee, action) => {
	switch (action.type) {
		case USER_LIST_REQUEST:
			return {
				loading: true,
			};

		case USER_LIST_SUCCESS:
			return {
				loading: false,
				users: action.payload,
			};

		case USER_LIST_FAIL:
			return {
				loading: false,
				error: action.payload,
			};
		case USER_LIST_RESET:
			return {
				users: [],
			};

		default:
			return state;
	}
};

export const userDeleteReducer = (state = {}, action) => {
	switch (action.type) {
		case USER_DELETE_REQUEST:
			return {
				loading: true,
			};

		case USER_DELETE_SUCCESS:
			return {
				loading: false,
				success: true,
			};

		case USER_DELETE_FAIL:
			return {
				loading: false,
				error: action.payload,
			};

		default:
			return state;
	}
};

export const userUpdateReducer = (state = { user: {} }, action) => {
	switch (action.type) {
		case USER_UPDATE_REQUEST:
			return {
				loading: true,
			};

		case USER_UPDATE_SUCCESS:
			return {
				loading: false,
				success: true,
			};

		case USER_UPDATE_FAIL:
			return {
				loading: false,
				error: action.payload,
			};
		default:
			return state;
	}
};

//Getting all user profile information for admin user
export const adminProfileListReducer = (state = { user: {} }, action) => {
	switch (action.type) {
		case USER_ADMIN_REQUEST:
			return {
				loading: true,
			};

		case USER_ADMIN_SUCCESS:
			return {
				loading: false,
				user: action.payload,
			};

		case USER_ADMIN_FAIL:
			return {
				loading: false,
				error: action.payload,
			};
		case USER_ADMIN_RESET:
			return {
				user: {},
			};

		default:
			return state;
	}
};
