import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import ErrorMessage from "./../../messages/ErrorMessage";
import { Link } from "react-router-dom";
import { Form, Button, Row, Col } from "react-bootstrap";
import Loading from "./../../messages/Loading";
import FormContainer from "./FormContainer/FormContainer.js";
import {
	register,
	getUserDetails,
	updateUserProfile,
} from "./../../actions/userActions";

const LoginScreen = ({ location, history }) => {
	const [password, setPassword] = useState("");
	const [email, setEmail] = useState("");
	const [name, setName] = useState("");
	const [message, setMessage] = useState(null);
	const [confirmPassword, setConfirmPassword] = useState("");
	const dispatch = useDispatch();
	const userDetails = useSelector((state) => state.userDetails);
	//user is the state of our userDetailsReducer
	const { loading, error, user } = userDetails;
	console.log(user);

	const updateUserProfile = useSelector((state) => state.updateUserProfile);
	const { success } = updateUserProfile;
	//getting the usreInfo from the userLogin state
	const userLogin = useSelector((state) => state.userLogin);
	const { userInfo } = userLogin;

	useEffect(() => {
		if (!userInfo) {
			history.push("/login");
		} else {
			if (!user.name) {
				dispatch(getUserDetails("profile"));
			} else {
				setName(user.name);
				setEmail(user.email);
			}
		}
	}, [history, userInfo, dispatch, user]);

	const submitHandler = (e) => {
		e.preventDefault();
		if (password !== confirmPassword) {
			setMessage("Passwords do not match");
		} else {
			dispatch(
				updateUserProfile({
					id: user._id,
					name,
					email,
					password,
				})
			);
		}
	};

	//userInfo is the token, if the token is available, redirect to homepage

	return (
		<Row>
			<Col md={3}>
				<h2> User Profile</h2>
				{message && <ErrorMessage> {message}</ErrorMessage>}
				{error && <ErrorMessage variant="danger">{error}</ErrorMessage>}
				{success && <ErrorMessage>Profile Successfully updated</ErrorMessage>}
				{loading && <Loading />}
				<Form onSubmit={submitHandler}>
					<Form.Group controlId="name">
						<Form.Label>Name</Form.Label>
						<Form.Control
							type="name"
							placeholder="Enter Name"
							value={name}
							onChange={(e) => setName(e.target.value)}
						></Form.Control>
					</Form.Group>

					<Form.Group controlId="email">
						<Form.Label>Email Address</Form.Label>
						<Form.Control
							type="email"
							placeholder="Enter Email"
							value={email}
							onChange={(e) => setEmail(e.target.value)}
						></Form.Control>
					</Form.Group>
					<Form.Group controlId="password">
						<Form.Label>Password</Form.Label>
						<Form.Control
							type="password"
							placeholder="Enter Password"
							value={password}
							onChange={(e) => setPassword(e.target.value)}
						></Form.Control>
					</Form.Group>

					<Form.Group controlId="confirmPassword">
						<Form.Label>Confirm Password</Form.Label>
						<Form.Control
							type="password"
							placeholder="Confirm Password"
							value={confirmPassword}
							onChange={(e) => setConfirmPassword(e.target.value)}
						></Form.Control>
					</Form.Group>

					<Button type="submit" variant="primary">
						UPDATE
					</Button>
				</Form>
				<Row className="py-3">
					<Col md={9}>
						<h2>My Orders</h2>
						<h1>PLS ORDER</h1>
					</Col>
				</Row>
			</Col>
		</Row>
	);
};

export default LoginScreen;
